import styled from '@emotion/styled'

export const StyledLayout = styled.div`
  width: 100%;
  height: 100vh;

`

export const LinesWrapper = styled.div`
  width: 90vw;
  height: 100%;
  margin: auto;
  position: absolute;
  top: 0;
  right: 0;
  left: 0;

`

export const Lines = styled.div`
  width: 1px;
  height: 100%;
  background: rgba(255, 255, 255, 0.1);
  overflow: hidden;
  position: absolute;
  top: 0;
  left: 50%;
  &:nth-child(1){
    margin-left: -25%;
    &:after{
      animation-delay: 2s;
    }
  }
  &:nth-child(3){
    margin-left: 25%;
    &:after{
      animation-delay: 2.5s;
    }
  }
  &:nth-child(4){
    margin-left: -50%;
    &:after{
      animation-delay: .5s;
    }
  }
  &:nth-child(5){
    margin-left: 50%;
    &:after{
      animation-delay: 1s;
    }
  }
  &:after{
    content: '';
    display: block;
    position: absolute;
    height: 15vh;
    width: 100%;
    top: -50%;
    left: 0;
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, #ffffff 75%, #ffffff 100%);
    animation: run 7s 0s infinite;
    animation-fill-mode: forwards;
    animation-timing-function: cubic-bezier(0.4, 0.26, 0, 0.97);
  }
`

export const MainLayout = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
  z-index: 10;

`
