import { Global, css } from '@emotion/core'

import { Header } from '../header';
import { Footer } from '../footer';

import {
  StyledLayout,
  LinesWrapper,
  Lines,
  MainLayout,
} from './layout.styles'


export const Layout = ({children}) => {
  return (
    <>
      <Global styles={css`
        body {
          overflow-x: hidden;
          overflow-y: auto;
          background-color: #071832;
          * {
            font-family: Space Mono,monospace;
            font-weight: 400;
          }
        }
      `} />
      <StyledLayout>
        <LinesWrapper>
          <Lines />
          <Lines />
          <Lines />
          <Lines />
          <Lines />
        </LinesWrapper>
        <Header />
        <MainLayout>
          {children}
        </MainLayout>
        <Footer />
      </StyledLayout>
    </>
  )
}
